	private static final int LEN_IDX = 0;
	private static final int CNT_IDX = 1;

	private Integer[] recursion(Integer prev, int curIdx, int[] nums, Map<Integer, Integer[]>[] record) {
		if (curIdx == nums.length) return new Integer[]{0, 0};
		else if (prev != null && record[curIdx] != null && record[curIdx].containsKey(prev)) return record[curIdx].get(prev).clone();

		Integer[] result = recursion(prev, curIdx + 1, nums, record);
		if (prev == null || prev < nums[curIdx]) {
			Integer[] selectedResult = recursion(nums[curIdx], curIdx + 1, nums, record);
			selectedResult[LEN_IDX] += 1;
			selectedResult[CNT_IDX] = selectedResult[CNT_IDX] == 0 ? 1 : selectedResult[CNT_IDX];

			if (result[LEN_IDX] == selectedResult[LEN_IDX]) result[CNT_IDX] += selectedResult[CNT_IDX];
			else if (result[LEN_IDX] < selectedResult[LEN_IDX]) result = selectedResult;
		}

		if (prev != null) {
			if(record[curIdx] == null) record[curIdx] = new HashMap<>();
			record[curIdx].put(prev, result);
		}
		return result.clone();
	}

	public int findNumberOfLIS(int[] nums) {
		if (nums == null || nums.length == 0) return 0;
		else if (nums.length == 1) return 1;

		Map<Integer, Integer[]>[] record = new HashMap[nums.length];

		Integer[] result = recursion(null, 0, nums, record);
		return result[CNT_IDX];
	}
